 4→const DEFAULT_WORKFLOW_DIR = ".tmp";
 5→const PLAN_FILENAME = "plan.md";
 6→const RESEARCH_FILENAME = "research.md";
 7→const STATE_FILENAME = "workflow-state.json";
 8→const REVIEW_CACHE_FILENAME = "plan-review.cache.json";
 9→const REVIEW_MARKDOWN_FILENAME = "plan-review.md";
10→const REVIEW_JSON_FILENAME = "plan-review.json";
11→
12→/**
13→ * Resolve the workflow directory for the current session.
14→ * Uses DOCUMENT_WORKFLOW_DIR env var if set, otherwise falls back to `.tmp/`.
15→ */
16→export function getWorkflowDir(cwd: string): string {
17→  const envDir = process.env.DOCUMENT_WORKFLOW_DIR;
18→  if (envDir && envDir.length > 0) {
19→    return resolve(cwd, expandTilde(envDir));
20→  }
21→  return resolve(cwd, DEFAULT_WORKFLOW_DIR);
22→}
23→
24→export function getPlanPath(cwd: string): string {
25→  return resolve(getWorkflowDir(cwd), PLAN_FILENAME);
26→}
27→
28→export function getResearchPath(cwd: string): string {
29→  return resolve(getWorkflowDir(cwd), RESEARCH_FILENAME);
30→}
31→
32→export function getStatePath(cwd: string): string {
33→  return resolve(getWorkflowDir(cwd), STATE_FILENAME);
34→}
35→
36→export function getReviewCachePath(cwd: string): string {
37→  return resolve(getWorkflowDir(cwd), REVIEW_CACHE_FILENAME);
38→}
39→
40→export function getReviewMarkdownPath(cwd: string): string {
41→  return resolve(getWorkflowDir(cwd), REVIEW_MARKDOWN_FILENAME);
42→}
43→
44→export function getReviewJsonPath(cwd: string): string {
45→  return resolve(getWorkflowDir(cwd), REVIEW_JSON_FILENAME);
46→}
47→
48→/**
49→ * Check if a given path is a workflow document (plan.md or research.md)
50→ * within the current session's workflow directory.
51→ */
52→export function isWorkflowDocumentPath(cwd: string, path: string): boolean {
53→  const normalized = resolve(cwd, expandTilde(path));
54→  return normalized === getPlanPath(cwd) || normalized === getResearchPath(cwd);
55→}
56→
57→/**
58→ * Check if a given absolute path is a plan.md within any workflow directory.
59→ * Matches both legacy (.tmp/plan.md) and session-based (.tmp/sessions/<id>/plan.md).
60→ */
61→export function isPlanFile(absolutePath: string): boolean {
62→  return absolutePath.endsWith(`/${PLAN_FILENAME}`);
63→}
64→
65→/**
66→ * Get the relative workflow dir path (for display/logging purposes).
67→ */
68→export function getWorkflowDirRelative(): string {
69→  const envDir = process.env.DOCUMENT_WORKFLOW_DIR;
70→  if (envDir && envDir.length > 0) {
71→    return envDir;
72→  }
73→  return DEFAULT_WORKFLOW_DIR;
74→}
