287→function extractMainCommand(words: string[]): { name: string; args: string[] } | null {
288→  let index = 0;
289→  while (index < words.length) {
290→    const token = words[index];
291→    if (!token) {
292→      index += 1;
293→      continue;
294→    }
295→    if (isVariableAssignment(token)) {
296→      index += 1;
297→      continue;
298→    }
299→    return {
300→      name: token,
301→      args: words.slice(index + 1),
302→    };
303→  }
304→  return null;
305→}
306→
307→function isVariableAssignment(token: string): boolean {
308→  return /^[A-Za-z_][A-Za-z0-9_]*=/.test(token);
309→}
310→
311→function extractRedirectionTargets(words: string[]): string[] {
312→  const targets: string[] = [];
313→
314→  for (let i = 0; i < words.length; i++) {
315→    const token = words[i];
316→    if (!token) {
317→      continue;
318→    }
319→
320→    if (isRedirectionToken(token)) {
321→      const next = words[i + 1];
322→      if (next && !next.startsWith("&")) {
323→        targets.push(next);
324→      }
325→      continue;
326→    }
327→
328→    const inline = token.match(/^(?:\d*>>?|\d*>\|)(.+)$/);
329→    if (inline?.[1] && !inline[1].startsWith("&")) {
330→      targets.push(inline[1]);
331→    }
332→  }
333→
334→  return targets;
335→}
336→
337→function isRedirectionToken(token: string): boolean {
338→  return /^(?:\d*>>?|\d*>\|)$/.test(token);
339→}
340→
341→function splitShellWords(command: string): string[] {
342→  const matches = command.match(/"[^"]*"|'[^']*'|\S+/g) || [];
343→  return matches.map((word) => stripQuotes(word));
344→}
345→
346→function stripQuotes(value: string): string {
347→  if (
348→    (value.startsWith('"') && value.endsWith('"')) ||
349→    (value.startsWith("'") && value.endsWith("'"))
350→  ) {
351→    return value.slice(1, -1);
352→  }
353→  return value;
354→}
355→
356→function computePlanHash(content: string): string {
357→  const stripped = content.replace(REVIEW_MARKER_REGEX, "").trimEnd();
358→  return createHash("sha256").update(stripped, "utf-8").digest("hex");
359→}
360→
361→function extractLatestAutoReviewMarker(content: string): AutoReviewMarker | null {
362→  const matches = content.match(REVIEW_MARKER_REGEX);
363→  if (!matches || matches.length === 0) {
364→    return null;
365→  }
366→
367→  const latest = matches[matches.length - 1];
368→  if (!latest) {
369→    return null;
370→  }
371→
372→  let verdict = "";
373→  let hash = "";
374→  const fields = latest.matchAll(/(\w+)=([^;]+)/g);
375→  for (const field of fields) {
376→    const key = field[1]?.trim();
377→    const value = field[2]?.trim();
378→    if (!key || !value) {
379→      continue;
380→    }
381→    if (key === "verdict") {
382→      verdict = value;
383→    } else if (key === "hash") {
384→      hash = value;
385→    }
386→  }
387→
388→  if (verdict.length === 0 || hash.length === 0) {
389→    return null;
390→  }
391→
392→  return { verdict, hash };
393→}
394→
395→function isRecord(value: unknown): value is Record<string, unknown> {
396→  return typeof value === "object" && value !== null;
397→}
398→
399→function dedupe(values: string[]): string[] {
400→  return [...new Set(values)];
401→}
402→
403→export default hook;
404→
405→if (import.meta.main) {
406→  const { runHook } = await import("cc-hooks-ts");
407→  await runHook(hook);
408→}
