     1â†’/**
     2â†’ * Platform-neutral webhook notification builder
     3â†’ *
     4â†’ * Extracts event processing logic (Stop/Notification branching,
     5â†’ * transcript extraction, footer construction) into a shared module.
     6â†’ * Discord/Slack adapters consume the output.
     7â†’ *
     8â†’ * Note: Permission notifications are NOT handled via events here.
     9â†’ * Notification(permission_prompt) may fire before PermissionRequest hooks
    10â†’ * complete (e.g. LLM evaluator), so permission webhooks are sent directly
    11â†’ * from the permission hook chain via webhook-sender.ts.
    12â†’ */
    13â†’
    14â†’import * as fs from "node:fs";
    15â†’import * as readline from "node:readline";
    16â†’
    17â†’import type { NotificationType } from "../../lib/notification-messages.ts";
    18â†’import { getGitContext } from "./git-context.ts";
    19â†’
    20â†’/** Platform-neutral notification message */
    21â†’export interface WebhookNotification {
    22â†’  title: string;
    23â†’  description: string;
    24â†’  severity: "success" | "warning" | "info" | "muted";
    25â†’  footer: string;
    26â†’}
    27â†’
    28â†’/** Hook input fields used by webhook notification logic */
    29â†’export interface HookInput {
    30â†’  hook_event_name: string;
    31â†’  session_id?: string;
    32â†’  cwd?: string;
    33â†’  notification_type?: NotificationType;
    34â†’  message?: string;
    35â†’  transcript_path?: string;
    36â†’  stop_hook_active?: boolean;
