     1â†’/**
     2â†’ * Platform-neutral webhook notification builder
     3â†’ *
     4â†’ * Extracts event processing logic (Stop/Notification branching,
     5â†’ * transcript extraction, footer construction) into a shared module.
     6â†’ * Discord/Slack adapters consume the output.
     7â†’ *
     8â†’ * Note: Permission notifications are NOT handled via events here.
     9â†’ * Notification(permission_prompt) may fire before PermissionRequest hooks
    10â†’ * complete (e.g. LLM evaluator), so permission webhooks are sent directly
    11â†’ * from the permission hook chain via webhook-sender.ts.
    12â†’ */
    13â†’
    14â†’import * as fs from "node:fs";
    15â†’import * as readline from "node:readline";
    16â†’
    17â†’import type { NotificationType } from "../../lib/notification-messages.ts";
    18â†’import { getGitContext } from "./git-context.ts";
    19â†’
    20â†’/** Platform-neutral notification message */
    21â†’export interface WebhookNotification {
    22â†’  title: string;
    23â†’  description: string;
    24â†’  severity: "success" | "warning" | "info" | "muted";
    25â†’  footer: string;
    26â†’}
    27â†’
    28â†’/** Hook input fields used by webhook notification logic */
    29â†’export interface HookInput {
    30â†’  hook_event_name: string;
    31â†’  session_id?: string;
    32â†’  cwd?: string;
    33â†’  notification_type?: NotificationType;
    34â†’  message?: string;
    35â†’  transcript_path?: string;
    36â†’  stop_hook_active?: boolean;
    37â†’  tool_name?: string;
    38â†’}
    39â†’
    40â†’/**
    41â†’ * Extract the last message of a given role from a JSONL transcript file.
    42â†’ * Reads the file then searches in reverse to find the most recent match.
    43â†’ */
    44â†’export async function extractFromTranscript(
    45â†’  transcriptPath: string,
    46â†’  role: "user" | "assistant",
    47â†’  limit: number,
    48â†’): Promise<string> {
    49â†’  if (!transcriptPath) return "";
    50â†’
    51â†’  const fileStream = fs.createReadStream(transcriptPath, { encoding: "utf-8" });
    52â†’  const rl = readline.createInterface({ input: fileStream });
    53â†’
    54â†’  const lines: string[] = [];
    55â†’  for await (const line of rl) {
    56â†’    lines.push(line);
    57â†’  }
    58â†’
    59â†’  for (let i = lines.length - 1; i >= 0; i--) {
    60â†’    const line = lines[i];
    61â†’    if (!line) continue;
    62â†’    try {
    63â†’      const entry = JSON.parse(line);
    64â†’      if (entry.type !== role) continue;
    65â†’
    66â†’      const content = entry.message?.content;
    67â†’      if (!content) continue;
    68â†’
    69â†’      let text = "";
    70â†’      if (typeof content === "string") {
    71â†’        text = content;
    72â†’      } else if (Array.isArray(content)) {
    73â†’        for (const block of content) {
    74â†’          if (typeof block === "string") {
    75â†’            text = block;
    76â†’            break;
    77â†’          }
    78â†’          if (block?.type === "text" && block.text) {
    79â†’            text = block.text;
    80â†’            break;
    81â†’          }
    82â†’        }
    83â†’      }
    84â†’
    85â†’      if (text) {
    86â†’        return text.length > limit ? `${text.slice(0, limit)}...` : text;
    87â†’      }
    88â†’    } catch {
    89â†’      // Skip malformed JSONL lines
    90â†’    }
    91â†’  }
    92â†’
    93â†’  return "";
    94â†’}
    95â†’
    96â†’/**
    97â†’ * Build a platform-neutral notification from hook input.
    98â†’ * Returns null when the event should be skipped (e.g. stop_hook_active, unrecognized event).
    99â†’ */
   100â†’export async function buildNotification(
   101â†’  input: HookInput,
