    }

    title = "âœ… è¿”ä¿¡å®Œäº†";
    severity = "success";

    const transcriptPath = input.transcript_path ?? "";
    const [userMsg, assistantMsg] = await Promise.all([
      extractFromTranscript(transcriptPath, "user", 100),
      extractFromTranscript(transcriptPath, "assistant", 300),
