  1→#!/usr/bin/env -S bun run --silent
  2→
  3→import { query } from "@anthropic-ai/claude-agent-sdk";
  4→import { createHash } from "node:crypto";
  5→import { existsSync, mkdirSync, readFileSync, writeFileSync } from "node:fs";
  6→import { dirname, resolve } from "node:path";
  7→import { defineHook } from "cc-hooks-ts";
  8→import { expandTilde } from "../lib/path-utils.ts";
  9→import {
 10→  isPlanFile,
 11→  resolveWorkflowPaths,
 12→} from "../lib/workflow-paths.ts";
 13→import "../types/tool-schemas.ts";
 14→
 15→type Severity = "P0" | "P1" | "P2";
 16→type Verdict = "pass" | "needs-work" | "blocker";
 17→
 18→interface Finding {
 19→  severity: Severity;
 20→  title: string;
 21→  detail: string;
 22→  suggestion: string;
 23→}
 24→
 25→interface ReviewerResult {
 26→  reviewer: string;
 27→  score: number;
 28→  summary: string;
 29→  findings: Finding[];
 30→}
 31→
 32→interface ReviewReport {
 33→  planPath: string;
 34→  reviewedAt: string;
 35→  model: string;
 36→  verdict: Verdict;
 37→  reviewers: ReviewerResult[];
 38→}
 39→
 40→interface CacheState {
 41→  planHash: string;
 42→  reviewedAt: string;
 43→}
 44→
 45→interface ReviewerSpec {
 46→  name: string;
 47→  focus: string;
 48→}
 49→
 50→interface AutoReviewMarker {
 51→  verdict: string;
 52→  hash: string;
 53→}
 54→
 55→interface QueryResult {
 56→  text: string;
 57→  structuredOutput?: unknown;
 58→  errorInfo?: string;
 59→}
 60→
 61→const REVIEW_RESULT_SCHEMA: Record<string, unknown> = {
 62→  type: "object",
 63→  properties: {
 64→    score: { type: "number" },
 65→    summary: { type: "string" },
 66→    findings: {
 67→      type: "array",
 68→      items: {
 69→        type: "object",
 70→        properties: {
 71→          severity: { type: "string", enum: ["P0", "P1", "P2"] },
 72→          title: { type: "string" },
 73→          detail: { type: "string" },
 74→          suggestion: { type: "string" },
 75→        },
 76→        required: ["severity", "title", "detail", "suggestion"],
 77→      },
 78→    },
 79→  },
 80→  required: ["score", "summary", "findings"],
 81→};
 82→
 83→const SYSTEM_PROMPT = `You are an automated plan reviewer.
 84→
 85→You MUST return ONLY a JSON object with this exact shape:
 86→{
 87→  "score": 1-5,
 88→  "summary": "string",
 89→  "findings": [
 90→    {
 91→      "severity": "P0|P1|P2",
 92→      "title": "string",
 93→      "detail": "string",
 94→      "suggestion": "string"
 95→    }
 96→  ]
 97→}
 98→
 99→Rules:
100→- No markdown output.
101→- Score 5 means excellent, 1 means unsafe.
102→- If no meaningful issue exists, return empty findings array.
103→- Focus on concrete, actionable findings only.`;
104→
105→const REVIEWERS: ReviewerSpec[] = [
106→  {
107→    name: "logic-validator",
108→    focus:
109→      "Check logical consistency, missing verification steps, incorrect assumptions, and contradiction between goals and tasks.",
110→  },
111→  {
112→    name: "architecture-boundary-analyzer",
113→    focus:
114→      "Check architecture boundaries, dependency direction, coupling risks, and contract design issues in the plan.",
115→  },
116→  {
117→    name: "release-safety-evaluator",
118→    focus:
119→      "Check release safety: test strategy, rollback readiness, rollout controls, and operational risks.",
120→  },
121→];
122→
123→const TARGET_TOOL_NAMES = new Set([
124→  "Write",
125→  "Edit",
126→  "MultiEdit",
127→  "NotebookEdit",
128→]);
129→const REVIEW_MARKER_REGEX = /<!--\s*auto-review:[^>]*-->/g;
130→const REVIEW_STATUS_REGEX =
131→  /^- Review Status:\s*(pending|pass|needs-work|blocker)\s*$/m;
132→
133→const hook = defineHook({
134→  trigger: { PostToolUse: true },
135→  run: async (context) => {
136→    const { tool_name, tool_input, cwd } = context.input;
137→    if (!TARGET_TOOL_NAMES.has(tool_name)) {
138→      return context.success({});
139→    }
140→
141→    const baseDir = getWorkingDirectory(cwd);
142→    const targetPath = extractTargetPath(tool_name, tool_input);
143→    if (!targetPath) {
144→      return context.success({});
145→    }
146→
147→    const absoluteTargetPath = normalizePath(baseDir, targetPath);
148→    if (!isPlanFile(absoluteTargetPath)) {
149→      return context.success({});
150→    }
151→
152→    if (!existsSync(absoluteTargetPath)) {
153→      return context.success({});
154→    }
155→
156→    const planContent = readFileSync(absoluteTargetPath, "utf-8");
157→    const planHash = computePlanHash(planContent);
158→    const existingMarker = extractLatestReviewMarker(planContent);
159→
160→    // Co-locate review output files with the plan file itself,
161→    // so session-scoped plans (.tmp/sessions/<id>/plan.md) get their
162→    // review artifacts in the same directory without relying on env vars.
163→    const planDir = dirname(absoluteTargetPath);
164→    mkdirSync(planDir, { recursive: true });
165→    const {
166→      reviewCache: cachePath,
167→      reviewMarkdown: markdownPath,
168→      reviewJson: jsonPath,
169→    } = resolveWorkflowPaths(planDir);
170→
171→    const previousCache = readCache(cachePath);
172→    const canSkipByCache =
173→      previousCache?.planHash === planHash &&
174→      existingMarker?.hash === planHash &&
175→      existingMarker.verdict.length > 0;
176→    if (canSkipByCache) {
177→      return context.success({});
178→    }
179→
180→    const model = process.env.PLAN_REVIEW_MODEL || "sonnet";
181→
182→    try {
183→      const reviewerResults = await Promise.all(
184→        REVIEWERS.map((reviewer) =>
185→          runReviewer(reviewer, planContent, absoluteTargetPath, model),
186→        ),
187→      );
188→
189→      const reviewedAt = new Date().toISOString();
190→      const report: ReviewReport = {
191→        planPath: absoluteTargetPath,
192→        reviewedAt,
193→        model,
194→        verdict: decideVerdict(reviewerResults),
195→        reviewers: reviewerResults,
196→      };
197→
198→      const withReviewStatus = upsertReviewStatus(planContent, report.verdict);
199→      const nextPlanHash = computePlanHash(withReviewStatus);
200→      const marker = buildReviewMarker(report, nextPlanHash);
201→      const updatedPlan = upsertReviewMarker(withReviewStatus, marker);
202→      if (updatedPlan !== planContent) {
203→        writeFileSync(absoluteTargetPath, updatedPlan, "utf-8");
204→      }
205→
206→      writeFileSync(markdownPath, buildReviewMarkdown(report), "utf-8");
207→      writeFileSync(jsonPath, JSON.stringify(report, null, 2), "utf-8");
208→      writeFileSync(
209→        cachePath,
210→        JSON.stringify(
211→          { planHash: nextPlanHash, reviewedAt } satisfies CacheState,
212→          null,
213→          2,
214→        ),
215→        "utf-8",
216→      );
217→    } catch (error) {
218→      const reason = error instanceof Error ? error.message : String(error);
219→      console.error(`[plan-review-automation] failed: ${reason}`);
220→    }
221→
222→    return context.success({});
223→  },
224→});
225→
226→function getWorkingDirectory(cwd: string | undefined): string {
227→  if (process.env.CLAUDE_TEST_CWD) {
228→    return process.env.CLAUDE_TEST_CWD;
229→  }
230→  if (typeof cwd === "string" && cwd.length > 0) {
231→    return cwd;
232→  }
233→  return process.cwd();
234→}
235→
236→function normalizePath(cwd: string, path: string): string {
237→  return resolve(cwd, expandTilde(path));
238→}
239→
240→function extractTargetPath(
241→  toolName: string,
242→  toolInput: unknown,
243→): string | null {
244→  if (!isRecord(toolInput)) {
245→    return null;
246→  }
247→
248→  if (
249→    (toolName === "Write" || toolName === "Edit" || toolName === "MultiEdit") &&
250→    typeof toolInput.file_path === "string"
251→  ) {
252→    return toolInput.file_path;
253→  }
254→
255→  if (
256→    toolName === "NotebookEdit" &&
257→    typeof toolInput.notebook_path === "string"
258→  ) {
259→    return toolInput.notebook_path;
260→  }
261→
262→  return null;
263→}
264→
265→function isRecord(value: unknown): value is Record<string, unknown> {
266→  return typeof value === "object" && value !== null;
267→}
268→
269→function hashText(text: string): string {
270→  return createHash("sha256").update(text, "utf-8").digest("hex");
271→}
272→
273→function stripReviewMarkers(content: string): string {
274→  return content.replace(REVIEW_MARKER_REGEX, "").trimEnd();
275→}
276→
277→function computePlanHash(planContent: string): string {
278→  return hashText(stripReviewMarkers(planContent));
279→}
280→
281→function extractLatestReviewMarker(content: string): AutoReviewMarker | null {
282→  const matches = content.match(REVIEW_MARKER_REGEX);
283→  if (!matches || matches.length === 0) {
284→    return null;
285→  }
286→
287→  const latest = matches[matches.length - 1];
288→  if (!latest) {
289→    return null;
290→  }
291→
292→  let verdict = "";
293→  let hash = "";
294→  for (const part of latest.matchAll(/(\w+)=([^;]+)/g)) {
295→    const key = part[1]?.trim();
296→    const value = part[2]?.trim();
297→    if (!key || !value) {
298→      continue;
299→    }
300→    if (key === "verdict") {
301→      verdict = value;
302→    }
303→    if (key === "hash") {
304→      hash = value;
305→    }
306→  }
307→
308→  if (verdict.length === 0 || hash.length === 0) {
309→    return null;
310→  }
311→
312→  return { verdict, hash };
313→}
314→
315→function readCache(path: string): CacheState | null {
316→  if (!existsSync(path)) {
317→    return null;
318→  }
319→
320→  try {
321→    const parsed = JSON.parse(readFileSync(path, "utf-8")) as CacheState;
322→    if (
323→      typeof parsed.planHash === "string" &&
324→      typeof parsed.reviewedAt === "string"
325→    ) {
326→      return parsed;
327→    }
328→  } catch {
329→    return null;
330→  }
331→
332→  return null;
333→}
334→
335→async function runReviewer(
336→  reviewer: ReviewerSpec,
337→  planContent: string,
338→  planPath: string,
339→  model: string,
340→): Promise<ReviewerResult> {
341→  const userPrompt = createReviewerPrompt(reviewer, planContent, planPath);
342→  const { text, structuredOutput, errorInfo } = await queryReviewer(
343→    userPrompt,
344→    model,
345→  );
346→
347→  if (errorInfo) {
348→    console.error(
349→      `[plan-review-automation] ${reviewer.name}: SDK error — ${errorInfo}`,
350→    );
351→    // Short-circuit: SDK errors produce no useful text to parse
352→    return createFallbackResult(
353→      reviewer.name,
354→      `SDK error: ${errorInfo}`,
355→      text || undefined,
356→    );
357→  }
358→
359→  if (structuredOutput !== undefined && structuredOutput !== null) {
360→    return parseStructuredOutput(reviewer.name, structuredOutput);
361→  }
362→
363→  if (text.length === 0) {
364→    console.error(
365→      `[plan-review-automation] ${reviewer.name}: empty response (no structured_output, no text)`,
366→    );
367→  }
368→
369→  return parseReviewerResult(reviewer.name, text);
370→}
371→
372→function createReviewerPrompt(
373→  reviewer: ReviewerSpec,
374→  planContent: string,
375→  planPath: string,
376→): string {
377→  return [
378→    `Reviewer: ${reviewer.name}`,
379→    `Focus: ${reviewer.focus}`,
380→    `Plan Path: ${planPath}`,
381→    "",
382→    "Review this implementation plan:",
383→    "<plan>",
384→    planContent,
385→    "</plan>",
386→  ].join("\n");
387→}
388→
389→async function queryReviewer(
390→  prompt: string,
391→  model: string,
392→): Promise<QueryResult> {
393→  const conversation = query({
394→    prompt,
395→    options: {
396→      model,
397→      maxTurns: 1,
398→      systemPrompt: SYSTEM_PROMPT,
399→      allowedTools: [],
400→      permissionMode: "bypassPermissions",
401→      allowDangerouslySkipPermissions: true,
402→      outputFormat: {
403→        type: "json_schema",
404→        schema: REVIEW_RESULT_SCHEMA,
405→      },
406→    },
407→  });
408→
409→  let assistantText = "";
410→  let resultText = "";
411→  let structuredOutput: unknown = undefined;
412→  let errorInfo: string | undefined;
413→
414→  for await (const message of conversation) {
415→    if (message.type === "assistant") {
416→      for (const block of message.message.content) {
417→        if (block.type === "text") {
418→          assistantText += block.text;
419→        }
420→      }
421→    } else if (message.type === "result") {
422→      if (message.subtype === "success") {
423→        structuredOutput = message.structured_output;
424→        if (message.result) {
425→          resultText = message.result;
426→        }
427→      } else {
428→        // SDKResultError: error_during_execution, error_max_turns,
429→        // error_max_budget_usd, error_max_structured_output_retries
430→        const errors =
431→          "errors" in message && Array.isArray(message.errors)
432→            ? (message.errors as string[]).join("; ")
433→            : "unknown";
434→        errorInfo = `${message.subtype}: ${errors}`;
435→      }
436→    }
437→  }
438→
439→  return {
440→    text: assistantText || resultText,
441→    structuredOutput,
442→    errorInfo,
443→  };
444→}
445→
446→function createFallbackResult(
447→  reviewer: string,
448→  reason: string,
449→  rawText?: string,
450→): ReviewerResult {
451→  const preview = rawText
452→    ? ` | text preview: "${rawText.slice(0, 200)}"`
453→    : "";
454→  console.error(
455→    `[plan-review-automation] ${reviewer}: parse failed — ${reason}${preview}`,
456→  );
457→  return {
458→    reviewer,
459→    score: 2,
460→    summary:
461→      "Reviewer response could not be parsed. Treat this as needs-work and run manual review.",
462→    findings: [
463→      {
464→        severity: "P1",
465→        title: "Review parse failure",
466→        detail: `Automated reviewer did not return valid JSON (${reason}). Validation quality is unknown.`,
467→        suggestion:
468→          "Run manual /self-review and /validate-plan before approving the plan.",
469→      },
470→    ],
471→  };
472→}
473→
474→function buildReviewerResult(
475→  reviewer: string,
476→  parsed: Record<string, unknown>,
477→): ReviewerResult {
478→  const score = normalizeScore(parsed.score);
479→  const summary =
480→    typeof parsed.summary === "string" && parsed.summary.length > 0
481→      ? parsed.summary
482→      : "No summary provided.";
483→  const findings = normalizeFindings(parsed.findings);
484→
485→  return { reviewer, score, summary, findings };
486→}
487→
488→function parseStructuredOutput(
489→  reviewer: string,
490→  output: unknown,
491→): ReviewerResult {
492→  if (!isRecord(output)) {
493→    return createFallbackResult(
494→      reviewer,
495→      `structured_output is ${typeof output}`,
496→    );
497→  }
498→  return buildReviewerResult(reviewer, output);
499→}
500→
501→function parseReviewerResult(
502→  reviewer: string,
503→  response: string,
504→): ReviewerResult {
505→  const jsonMatch = response.match(/\{[\s\S]*\}/);
506→  if (!jsonMatch) {
507→    return createFallbackResult(
508→      reviewer,
509→      `no JSON found in text response (length=${response.length})`,
510→      response,
511→    );
512→  }
513→
514→  try {
515→    const parsed = JSON.parse(jsonMatch[0]) as Record<string, unknown>;
516→    return buildReviewerResult(reviewer, parsed);
517→  } catch {
518→    return createFallbackResult(
519→      reviewer,
520→      "JSON.parse failed on extracted text",
521→      response,
522→    );
523→  }
524→}
525→
526→function normalizeScore(value: unknown): number {
527→  if (typeof value !== "number" || !Number.isFinite(value)) {
528→    return 3;
529→  }
530→  const rounded = Math.round(value);
531→  if (rounded < 1) return 1;
532→  if (rounded > 5) return 5;
533→  return rounded;
534→}
535→
536→function normalizeFindings(value: unknown): Finding[] {
537→  if (!Array.isArray(value)) {
538→    return [];
539→  }
540→
541→  const findings: Finding[] = [];
542→  for (const item of value) {
543→    if (!isRecord(item)) {
544→      continue;
545→    }
546→    const severity = normalizeSeverity(item.severity);
547→    if (!severity) {
548→      continue;
549→    }
550→    const title = typeof item.title === "string" ? item.title : "";
551→    const detail = typeof item.detail === "string" ? item.detail : "";
552→    const suggestion =
553→      typeof item.suggestion === "string" ? item.suggestion : "";
554→    if (title.length === 0 || detail.length === 0) {
555→      continue;
556→    }
557→    findings.push({ severity, title, detail, suggestion });
558→  }
559→  return findings;
560→}
561→
562→function normalizeSeverity(value: unknown): Severity | null {
563→  if (value === "P0" || value === "P1" || value === "P2") {
564→    return value;
565→  }
566→  return null;
567→}
568→
569→function decideVerdict(results: ReviewerResult[]): Verdict {
570→  const severities = results.flatMap((result) =>
571→    result.findings.map((finding) => finding.severity),
572→  );
573→  if (severities.includes("P0")) {
574→    return "blocker";
575→  }
576→  if (severities.includes("P1")) {
577→    return "needs-work";
578→  }
579→  return "pass";
580→}
581→
582→function buildReviewMarker(report: ReviewReport, planHash: string): string {
583→  const reviewers = report.reviewers
584→    .map((reviewer) => reviewer.reviewer)
585→    .join(",");
586→  return `<!-- auto-review: verdict=${report.verdict}; hash=${planHash}; at=${report.reviewedAt}; reviewers=${reviewers} -->`;
587→}
588→
589→function upsertReviewMarker(planContent: string, marker: string): string {
590→  const withoutMarker = stripReviewMarkers(planContent).trimEnd();
591→  return `${withoutMarker}\n\n${marker}\n`;
592→}
593→
594→function upsertReviewStatus(planContent: string, verdict: Verdict): string {
595→  const statusLine = `- Review Status: ${verdict}`;
596→  const withoutMarker = stripReviewMarkers(planContent);
597→  if (REVIEW_STATUS_REGEX.test(withoutMarker)) {
598→    return withoutMarker.replace(REVIEW_STATUS_REGEX, statusLine);
599→  }
600→
601→  const lines = withoutMarker.split("\n");
602→  const approvalIndex = lines.findIndex(
603→    (line) => line.trim() === "## Approval",
604→  );
605→  if (approvalIndex === -1) {
606→    return withoutMarker;
607→  }
608→
609→  let insertIndex = approvalIndex + 1;
610→  for (let i = approvalIndex + 1; i < lines.length; i++) {
611→    const line = lines[i];
612→    if (typeof line !== "string") {
613→      continue;
614→    }
615→    const trimmed = line.trim();
616→    if (trimmed.startsWith("## ")) {
617→      break;
618→    }
619→    if (trimmed.startsWith("- Plan Status:")) {
620→      insertIndex = i + 1;
621→      break;
622→    }
623→  }
624→
625→  lines.splice(insertIndex, 0, statusLine);
626→  return lines.join("\n");
627→}
628→
629→function buildReviewMarkdown(report: ReviewReport): string {
630→  const lines: string[] = [];
631→  lines.push("# Automated Plan Review");
632→  lines.push("");
633→  lines.push(`- Plan: \`${report.planPath}\``);
634→  lines.push(`- Reviewed At: ${report.reviewedAt}`);
635→  lines.push(`- Model: ${report.model}`);
636→  lines.push(`- Verdict: **${report.verdict}**`);
637→  lines.push("");
638→
639→  for (const reviewer of report.reviewers) {
640→    lines.push(`## ${reviewer.reviewer}`);
641→    lines.push(`- Score: ${reviewer.score}/5`);
642→    lines.push(`- Summary: ${reviewer.summary}`);
643→    lines.push("");
644→
645→    if (reviewer.findings.length === 0) {
646→      lines.push("- Findings: none");
647→      lines.push("");
648→      continue;
649→    }
650→
651→    lines.push("### Findings");
652→    for (const finding of reviewer.findings) {
653→      lines.push(`- [${finding.severity}] ${finding.title}`);
654→      lines.push(`  - Detail: ${finding.detail}`);
655→      if (finding.suggestion.length > 0) {
656→        lines.push(`  - Suggestion: ${finding.suggestion}`);
657→      }
658→    }
659→    lines.push("");
660→  }
661→
662→  return `${lines.join("\n").trim()}\n`;
663→}
664→
665→export {
666→  buildReviewMarkdown,
667→  buildReviewMarker,
668→  buildReviewerResult,
669→  computePlanHash,
670→  createFallbackResult,
671→  decideVerdict,
672→  extractTargetPath,
673→  extractLatestReviewMarker,
674→  isPlanFile,
675→  parseReviewerResult,
676→  parseStructuredOutput,
677→  stripReviewMarkers,
678→  upsertReviewMarker,
679→  upsertReviewStatus,
680→};
681→
682→export default hook;
683→
684→if (import.meta.main) {
685→  const { runHook } = await import("cc-hooks-ts");
686→  await runHook(hook);
687→}
688→

