 64→    score: { type: "number" },
 65→    summary: { type: "string" },
 66→    findings: {
 67→      type: "array",
 68→      items: {
 69→        type: "object",
 70→        properties: {
 71→          severity: { type: "string", enum: ["P0", "P1", "P2"] },
 72→          title: { type: "string" },
 73→          detail: { type: "string" },
 74→          suggestion: { type: "string" },
 75→        },
 76→        required: ["severity", "title", "detail", "suggestion"],
 77→      },
 78→    },
 79→  },
 80→  required: ["score", "summary", "findings"],
 81→};
 82→
 83→const SYSTEM_PROMPT = `You are an automated plan reviewer.
 84→
 85→You MUST return ONLY a JSON object with this exact shape:
 86→{
 87→  "score": 1-5,
 88→  "summary": "string",
 89→  "findings": [
 90→    {
 91→      "severity": "P0|P1|P2",
 92→      "title": "string",
 93→      "detail": "string",
 94→      "suggestion": "string"
 95→    }
 96→  ]
 97→}
 98→
 99→Rules:
100→- Respond in a SINGLE turn with the JSON object. Do NOT use tools or request additional turns.
101→- No markdown output.
102→- Score 5 means excellent, 1 means unsafe.
103→- If no meaningful issue exists, return empty findings array.
104→- Focus on concrete, actionable findings only.`;
105→
106→const REVIEWERS: ReviewerSpec[] = [
107→  {
108→    name: "logic-validator",
109→    focus:
110→      "Check logical consistency, missing verification steps, incorrect assumptions, and contradiction between goals and tasks.",
111→  },
112→  {
113→    name: "architecture-boundary-analyzer",
114→    focus:
115→      "Check architecture boundaries, dependency direction, coupling risks, and contract design issues in the plan.",
116→  },
117→  {
118→    name: "release-safety-evaluator",
119→    focus:
120→      "Check release safety: test strategy, rollback readiness, rollout controls, and operational risks.",
121→  },
122→];
123→
124→const TARGET_TOOL_NAMES = new Set([
125→  "Write",
126→  "Edit",
127→  "MultiEdit",
128→  "NotebookEdit",
129→]);
130→const REVIEW_MARKER_REGEX = /<!--\s*auto-review:[^>]*-->/g;
131→const REVIEW_STATUS_REGEX =
132→  /^- Review Status:\s*(pending|pass|needs-work|blocker)\s*$/m;
133→
134→const hook = defineHook({
135→  trigger: { PostToolUse: true },
136→  run: async (context) => {
137→    const { tool_name, tool_input, cwd } = context.input;
138→    if (!TARGET_TOOL_NAMES.has(tool_name)) {
139→      return context.success({});
140→    }
141→
142→    const baseDir = getWorkingDirectory(cwd);
143→    const targetPath = extractTargetPath(tool_name, tool_input);
144→    if (!targetPath) {
145→      return context.success({});
146→    }
147→
148→    const absoluteTargetPath = normalizePath(baseDir, targetPath);
149→    if (!isPlanFile(absoluteTargetPath)) {
150→      return context.success({});
151→    }
152→
153→    if (!existsSync(absoluteTargetPath)) {
154→      return context.success({});
155→    }
156→
157→    const planContent = readFileSync(absoluteTargetPath, "utf-8");
158→    const planHash = computePlanHash(planContent);
159→    const existingMarker = extractLatestReviewMarker(planContent);
160→
161→    // Co-locate review output files with the plan file itself,
162→    // so session-scoped plans (.tmp/sessions/<id>/plan.md) get their
163→    // review artifacts in the same directory without relying on env vars.
164→    const planDir = dirname(absoluteTargetPath);
165→    mkdirSync(planDir, { recursive: true });
166→    const {
167→      reviewCache: cachePath,
168→      reviewMarkdown: markdownPath,
169→      reviewJson: jsonPath,
170→    } = resolveWorkflowPaths(planDir);
171→
172→    const previousCache = readCache(cachePath);
173→    const canSkipByCache =
174→      previousCache?.planHash === planHash &&
175→      existingMarker?.hash === planHash &&
176→      existingMarker.verdict.length > 0;
177→    if (canSkipByCache) {
178→      return context.success({});
179→    }
180→
181→    const model = process.env.PLAN_REVIEW_MODEL || "sonnet";
182→
183→    // Read sibling research.md to provide project investigation context
184→    const { research: researchPath } = resolveWorkflowPaths(planDir);
185→    const researchContent =
186→      existsSync(researchPath) ? readFileSync(researchPath, "utf-8") : null;
187→
188→    try {
189→      const reviewerResults = await Promise.all(
190→        REVIEWERS.map((reviewer) =>
191→          runReviewer(
192→            reviewer,
193→            planContent,
194→            absoluteTargetPath,
195→            model,
196→            baseDir,
197→            researchContent,
198→          ),
199→        ),
200→      );
201→
202→      const reviewedAt = new Date().toISOString();
203→      const report: ReviewReport = {
204→        planPath: absoluteTargetPath,
205→        reviewedAt,
206→        model,
207→        verdict: decideVerdict(reviewerResults),
208→        reviewers: reviewerResults,
209→      };
210→
211→      const withReviewStatus = upsertReviewStatus(planContent, report.verdict);
212→      const nextPlanHash = computePlanHash(withReviewStatus);
213→      const marker = buildReviewMarker(report, nextPlanHash);
214→      const updatedPlan = upsertReviewMarker(withReviewStatus, marker);
215→      if (updatedPlan !== planContent) {
216→        writeFileSync(absoluteTargetPath, updatedPlan, "utf-8");
217→      }
218→
219→      writeFileSync(markdownPath, buildReviewMarkdown(report), "utf-8");
220→      writeFileSync(jsonPath, JSON.stringify(report, null, 2), "utf-8");
221→      writeFileSync(
222→        cachePath,
223→        JSON.stringify(
224→          { planHash: nextPlanHash, reviewedAt } satisfies CacheState,
225→          null,
226→          2,
227→        ),
228→        "utf-8",
229→      );
230→    } catch (error) {
231→      const reason = error instanceof Error ? error.message : String(error);
232→      console.error(`[plan-review-automation] failed: ${reason}`);
233→    }
234→
235→    return context.success({});
236→  },
237→});
238→
239→function getWorkingDirectory(cwd: string | undefined): string {
240→  if (process.env.CLAUDE_TEST_CWD) {
241→    return process.env.CLAUDE_TEST_CWD;
242→  }
243→  if (typeof cwd === "string" && cwd.length > 0) {
244→    return cwd;
245→  }
246→  return process.cwd();
247→}
248→
249→function normalizePath(cwd: string, path: string): string {
250→  return resolve(cwd, expandTilde(path));
251→}
252→
253→function extractTargetPath(
254→  toolName: string,
255→  toolInput: unknown,
256→): string | null {
257→  if (!isRecord(toolInput)) {
258→    return null;
259→  }
260→
261→  if (
262→    (toolName === "Write" || toolName === "Edit" || toolName === "MultiEdit") &&
263→    typeof toolInput.file_path === "string"
264→  ) {
265→    return toolInput.file_path;
266→  }
267→
268→  if (
269→    toolName === "NotebookEdit" &&
270→    typeof toolInput.notebook_path === "string"
271→  ) {
272→    return toolInput.notebook_path;
273→  }
274→
275→  return null;
276→}
277→
278→function isRecord(value: unknown): value is Record<string, unknown> {
279→  return typeof value === "object" && value !== null;
280→}
281→
282→function hashText(text: string): string {
283→  return createHash("sha256").update(text, "utf-8").digest("hex");
284→}
285→
286→function stripReviewMarkers(content: string): string {
287→  return content.replace(REVIEW_MARKER_REGEX, "").trimEnd();
288→}
289→
290→function computePlanHash(planContent: string): string {
291→  return hashText(stripReviewMarkers(planContent));
292→}
293→
294→function extractLatestReviewMarker(content: string): AutoReviewMarker | null {
295→  const matches = content.match(REVIEW_MARKER_REGEX);
296→  if (!matches || matches.length === 0) {
297→    return null;
298→  }
299→
300→  const latest = matches[matches.length - 1];
301→  if (!latest) {
302→    return null;
303→  }
304→
305→  let verdict = "";
306→  let hash = "";
307→  for (const part of latest.matchAll(/(\w+)=([^;]+)/g)) {
308→    const key = part[1]?.trim();
309→    const value = part[2]?.trim();
310→    if (!key || !value) {
311→      continue;
312→    }
313→    if (key === "verdict") {
314→      verdict = value;
315→    }
316→    if (key === "hash") {
317→      hash = value;
318→    }
319→  }
320→
321→  if (verdict.length === 0 || hash.length === 0) {
322→    return null;
323→  }
324→
325→  return { verdict, hash };
326→}
327→
328→function readCache(path: string): CacheState | null {
329→  if (!existsSync(path)) {
330→    return null;
331→  }
332→
333→  try {
334→    const parsed = JSON.parse(readFileSync(path, "utf-8")) as CacheState;
335→    if (
336→      typeof parsed.planHash === "string" &&
337→      typeof parsed.reviewedAt === "string"
338→    ) {
339→      return parsed;
340→    }
341→  } catch {
342→    return null;
343→  }
344→
345→  return null;
346→}
347→
348→async function runReviewer(
349→  reviewer: ReviewerSpec,
350→  planContent: string,
351→  planPath: string,
352→  model: string,
353→  cwd: string,
354→  researchContent: string | null,
355→): Promise<ReviewerResult> {
356→  const userPrompt = createReviewerPrompt(
357→    reviewer,
358→    planContent,
359→    planPath,
360→    researchContent,
361→  );
362→  const { text, structuredOutput, errorInfo } = await queryReviewer(
363→    userPrompt,
364→    model,
365→    cwd,
366→  );
367→
368→  if (errorInfo) {
369→    console.error(
370→      `[plan-review-automation] ${reviewer.name}: SDK error — ${errorInfo}`,
371→    );
372→    // Short-circuit: SDK errors produce no useful text to parse
373→    return createFallbackResult(
374→      reviewer.name,
375→      `SDK error: ${errorInfo}`,
376→      text || undefined,
377→    );
378→  }
379→
380→  if (structuredOutput !== undefined && structuredOutput !== null) {
381→    return parseStructuredOutput(reviewer.name, structuredOutput);
382→  }
383→
384→  if (text.length === 0) {
385→    console.error(
386→      `[plan-review-automation] ${reviewer.name}: empty response (no structured_output, no text)`,
387→    );
388→  }
389→
390→  return parseReviewerResult(reviewer.name, text);
391→}
392→
393→function createReviewerPrompt(
394→  reviewer: ReviewerSpec,
395→  planContent: string,
396→  planPath: string,
397→  researchContent: string | null,
398→): string {
399→  const sections = [
400→    `Reviewer: ${reviewer.name}`,
401→    `Focus: ${reviewer.focus}`,
402→    `Plan Path: ${planPath}`,
403→    "",
404→  ];
405→
406→  if (researchContent) {
407→    sections.push(
408→      "Project research context (investigation findings that inform the plan):",
409→      "<research>",
410→      researchContent,
411→      "</research>",
412→      "",
413→    );
414→  }
415→
416→  sections.push(
417→    "Review this implementation plan:",
418→    "<plan>",
419→    planContent,
420→    "</plan>",
421→  );
422→
423→  return sections.join("\n");
424→}
425→
426→async function queryReviewer(
427→  prompt: string,
428→  model: string,
429→  cwd: string,
430→): Promise<QueryResult> {
431→  const conversation = query({
432→    prompt,
433→    options: {
434→      cwd,
435→      model,
436→      // Each reviewer should complete in 1-3 turns (no tools enabled).
437→      // Hard cap at 10 to tolerate structured output validation retries
438→      // without hitting error_max_turns.
439→      maxTurns: 10,
440→      systemPrompt: SYSTEM_PROMPT,
441→      allowedTools: [],
442→      permissionMode: "bypassPermissions",
443→      allowDangerouslySkipPermissions: true,
444→      outputFormat: {
445→        type: "json_schema",
446→        schema: REVIEW_RESULT_SCHEMA,
447→      },
448→    },
449→  });
450→
451→  let assistantText = "";
452→  let resultText = "";
453→  let structuredOutput: unknown = undefined;
454→  let errorInfo: string | undefined;
455→
456→  for await (const message of conversation) {
457→    if (message.type === "assistant") {
458→      for (const block of message.message.content) {
459→        if (block.type === "text") {
460→          assistantText += block.text;
461→        }
462→      }
463→    } else if (message.type === "result") {
464→      if (message.subtype === "success") {
465→        structuredOutput = message.structured_output;
466→        if (message.result) {
467→          resultText = message.result;
468→        }
469→      } else {
470→        // SDKResultError: error_during_execution, error_max_turns,
471→        // error_max_budget_usd, error_max_structured_output_retries
472→        const errors =
473→          "errors" in message && Array.isArray(message.errors)
474→            ? (message.errors as string[]).join("; ")
475→            : "unknown";
476→        errorInfo = `${message.subtype}: ${errors}`;
477→      }
478→    }
479→  }
480→
481→  return {
482→    text: assistantText || resultText,
483→    structuredOutput,
484→    errorInfo,
485→  };
486→}
487→
488→function createFallbackResult(
489→  reviewer: string,
490→  reason: string,
491→  rawText?: string,
492→): ReviewerResult {
493→  const preview = rawText
494→    ? ` | text preview: "${rawText.slice(0, 200)}"`
495→    : "";
496→  console.error(
497→    `[plan-review-automation] ${reviewer}: parse failed — ${reason}${preview}`,
498→  );
499→  return {
500→    reviewer,
501→    score: 2,
502→    summary:
503→      "Reviewer response could not be parsed. Treat this as needs-work and run manual review.",
504→    findings: [
505→      {
506→        severity: "P1",
507→        title: "Review parse failure",
508→        detail: `Automated reviewer did not return valid JSON (${reason}). Validation quality is unknown.`,
509→        suggestion:
510→          "Run manual /self-review and /validate-plan before approving the plan.",
511→      },
512→    ],
513→  };
514→}
515→
516→function buildReviewerResult(
517→  reviewer: string,
518→  parsed: Record<string, unknown>,
519→): ReviewerResult {
520→  const score = normalizeScore(parsed.score);
521→  const summary =
522→    typeof parsed.summary === "string" && parsed.summary.length > 0
523→      ? parsed.summary
524→      : "No summary provided.";
525→  const findings = normalizeFindings(parsed.findings);
526→
527→  return { reviewer, score, summary, findings };
528→}
529→
530→function parseStructuredOutput(
531→  reviewer: string,
532→  output: unknown,
533→): ReviewerResult {
534→  if (!isRecord(output)) {
535→    return createFallbackResult(
536→      reviewer,
537→      `structured_output is ${typeof output}`,
538→    );
539→  }
540→  return buildReviewerResult(reviewer, output);
541→}
542→
543→function parseReviewerResult(
544→  reviewer: string,
545→  response: string,
546→): ReviewerResult {
547→  const jsonMatch = response.match(/\{[\s\S]*\}/);
548→  if (!jsonMatch) {
549→    return createFallbackResult(
550→      reviewer,
551→      `no JSON found in text response (length=${response.length})`,
552→      response,
553→    );
554→  }
555→
556→  try {
557→    const parsed = JSON.parse(jsonMatch[0]) as Record<string, unknown>;
558→    return buildReviewerResult(reviewer, parsed);
559→  } catch {
560→    return createFallbackResult(
561→      reviewer,
562→      "JSON.parse failed on extracted text",
563→      response,
564→    );
565→  }
566→}
567→
568→function normalizeScore(value: unknown): number {
569→  if (typeof value !== "number" || !Number.isFinite(value)) {
570→    return 3;
571→  }
572→  const rounded = Math.round(value);
573→  if (rounded < 1) return 1;
574→  if (rounded > 5) return 5;
575→  return rounded;
576→}
577→
578→function normalizeFindings(value: unknown): Finding[] {
579→  if (!Array.isArray(value)) {
580→    return [];
581→  }
582→
583→  const findings: Finding[] = [];
584→  for (const item of value) {
585→    if (!isRecord(item)) {
586→      continue;
587→    }
588→    const severity = normalizeSeverity(item.severity);
589→    if (!severity) {
590→      continue;
591→    }
592→    const title = typeof item.title === "string" ? item.title : "";
593→    const detail = typeof item.detail === "string" ? item.detail : "";
594→    const suggestion =
595→      typeof item.suggestion === "string" ? item.suggestion : "";
596→    if (title.length === 0 || detail.length === 0) {
597→      continue;
598→    }
599→    findings.push({ severity, title, detail, suggestion });
600→  }
601→  return findings;
602→}
603→
604→function normalizeSeverity(value: unknown): Severity | null {
605→  if (value === "P0" || value === "P1" || value === "P2") {
606→    return value;
607→  }
608→  return null;
609→}
610→
611→function decideVerdict(results: ReviewerResult[]): Verdict {
612→  const severities = results.flatMap((result) =>
613→    result.findings.map((finding) => finding.severity),
614→  );
615→  if (severities.includes("P0")) {
616→    return "blocker";
617→  }
618→  if (severities.includes("P1")) {
619→    return "needs-work";
620→  }
621→  return "pass";
622→}
623→
624→function buildReviewMarker(report: ReviewReport, planHash: string): string {
625→  const reviewers = report.reviewers
626→    .map((reviewer) => reviewer.reviewer)
627→    .join(",");
628→  return `<!-- auto-review: verdict=${report.verdict}; hash=${planHash}; at=${report.reviewedAt}; reviewers=${reviewers} -->`;
629→}
630→
631→function upsertReviewMarker(planContent: string, marker: string): string {
632→  const withoutMarker = stripReviewMarkers(planContent).trimEnd();
633→  return `${withoutMarker}\n\n${marker}\n`;
634→}
635→
636→function upsertReviewStatus(planContent: string, verdict: Verdict): string {
637→  const statusLine = `- Review Status: ${verdict}`;
638→  const withoutMarker = stripReviewMarkers(planContent);
639→  if (REVIEW_STATUS_REGEX.test(withoutMarker)) {
640→    return withoutMarker.replace(REVIEW_STATUS_REGEX, statusLine);
641→  }
642→
643→  const lines = withoutMarker.split("\n");
644→  const approvalIndex = lines.findIndex(
645→    (line) => line.trim() === "## Approval",
646→  );
647→  if (approvalIndex === -1) {
648→    return withoutMarker;
649→  }
650→
651→  let insertIndex = approvalIndex + 1;
652→  for (let i = approvalIndex + 1; i < lines.length; i++) {
653→    const line = lines[i];
654→    if (typeof line !== "string") {
655→      continue;
656→    }
657→    const trimmed = line.trim();
658→    if (trimmed.startsWith("## ")) {
659→      break;
660→    }
661→    if (trimmed.startsWith("- Plan Status:")) {
662→      insertIndex = i + 1;
663→      break;
664→    }
665→  }
666→
667→  lines.splice(insertIndex, 0, statusLine);
668→  return lines.join("\n");
669→}
670→
671→function buildReviewMarkdown(report: ReviewReport): string {
672→  const lines: string[] = [];
673→  lines.push("# Automated Plan Review");
674→  lines.push("");
675→  lines.push(`- Plan: \`${report.planPath}\``);
676→  lines.push(`- Reviewed At: ${report.reviewedAt}`);
677→  lines.push(`- Model: ${report.model}`);
678→  lines.push(`- Verdict: **${report.verdict}**`);
679→  lines.push("");
680→
681→  for (const reviewer of report.reviewers) {
682→    lines.push(`## ${reviewer.reviewer}`);
683→    lines.push(`- Score: ${reviewer.score}/5`);
684→    lines.push(`- Summary: ${reviewer.summary}`);
685→    lines.push("");
686→
687→    if (reviewer.findings.length === 0) {
688→      lines.push("- Findings: none");
689→      lines.push("");
690→      continue;
691→    }
692→
693→    lines.push("### Findings");
694→    for (const finding of reviewer.findings) {
695→      lines.push(`- [${finding.severity}] ${finding.title}`);
696→      lines.push(`  - Detail: ${finding.detail}`);
697→      if (finding.suggestion.length > 0) {
698→        lines.push(`  - Suggestion: ${finding.suggestion}`);
699→      }
700→    }
701→    lines.push("");
702→  }
703→
704→  return `${lines.join("\n").trim()}\n`;
705→}
706→
707→export {
708→  buildReviewMarkdown,
709→  buildReviewMarker,
710→  buildReviewerResult,
711→  computePlanHash,
712→  createFallbackResult,
713→  decideVerdict,
714→  extractTargetPath,
715→  extractLatestReviewMarker,
716→  isPlanFile,
717→  parseReviewerResult,
718→  parseStructuredOutput,
719→  stripReviewMarkers,
720→  upsertReviewMarker,
721→  upsertReviewStatus,
722→};
723→
724→export default hook;
725→
726→if (import.meta.main) {
727→  const { runHook } = await import("cc-hooks-ts");
728→  await runHook(hook);
729→}
