230→function getWorkingDirectory(cwd: string | undefined): string {
231→  if (process.env.CLAUDE_TEST_CWD) {
232→    return process.env.CLAUDE_TEST_CWD;
233→  }
234→  if (typeof cwd === "string" && cwd.length > 0) {
235→    return cwd;
236→  }
237→  return process.cwd();
238→}
239→
240→function normalizePath(cwd: string, path: string): string {
241→  return resolve(cwd, expandTilde(path));
242→}
