244→function extractTargetPath(
245→  toolName: string,
246→  toolInput: unknown,
247→): string | null {
248→  if (!isRecord(toolInput)) {
249→    return null;
250→  }
251→
252→  if (
253→    (toolName === "Write" || toolName === "Edit" || toolName === "MultiEdit") &&
254→    typeof toolInput.file_path === "string"
255→  ) {
256→    return toolInput.file_path;
257→  }
258→
259→  if (
260→    toolName === "NotebookEdit" &&
261→    typeof toolInput.notebook_path === "string"
262→  ) {
263→    return toolInput.notebook_path;
264→  }
265→
266→  return null;
267→}
268→
269→function isRecord(value: unknown): value is Record<string, unknown> {
270→  return typeof value === "object" && value !== null;
271→}
272→
273→function hashText(text: string): string {
274→  return createHash("sha256").update(text, "utf-8").digest("hex");
275→}
276→
277→function stripReviewMarkers(content: string): string {
278→  return content.replace(REVIEW_MARKER_REGEX, "").trimEnd();
279→}
280→
281→function computePlanHash(planContent: string): string {
282→  return hashText(stripReviewMarkers(planContent));
283→}
