285→function extractLatestReviewMarker(content: string): AutoReviewMarker | null {
286→  const matches = content.match(REVIEW_MARKER_REGEX);
287→  if (!matches || matches.length === 0) {
288→    return null;
289→  }
290→
291→  const latest = matches[matches.length - 1];
292→  if (!latest) {
293→    return null;
294→  }
295→
296→  let verdict = "";
297→  let hash = "";
298→  for (const part of latest.matchAll(/(\w+)=([^;]+)/g)) {
299→    const key = part[1]?.trim();
300→    const value = part[2]?.trim();
301→    if (!key || !value) {
302→      continue;
303→    }
304→    if (key === "verdict") {
305→      verdict = value;
306→    }
307→    if (key === "hash") {
308→      hash = value;
309→    }
310→  }
311→
312→  if (verdict.length === 0 || hash.length === 0) {
313→    return null;
314→  }
315→
316→  return { verdict, hash };
317→}
