319→function readCache(path: string): CacheState | null {
320→  if (!existsSync(path)) {
321→    return null;
322→  }
323→
324→  try {
325→    const parsed = JSON.parse(readFileSync(path, "utf-8")) as CacheState;
326→    if (
327→      typeof parsed.planHash === "string" &&
328→      typeof parsed.reviewedAt === "string"
329→    ) {
330→      return parsed;
331→    }
332→  } catch {
333→    return null;
334→  }
335→
336→  return null;
337→}
