585→  const statusLine = `- Review Status: ${verdict}`;
586→  const withoutMarker = stripReviewMarkers(planContent);
587→  if (REVIEW_STATUS_REGEX.test(withoutMarker)) {
588→    return withoutMarker.replace(REVIEW_STATUS_REGEX, statusLine);
589→  }
590→
591→  const lines = withoutMarker.split("\n");
592→  const approvalIndex = lines.findIndex(
593→    (line) => line.trim() === "## Approval",
594→  );
595→  if (approvalIndex === -1) {
596→    return withoutMarker;
597→  }
598→
599→  let insertIndex = approvalIndex + 1;
600→  for (let i = approvalIndex + 1; i < lines.length; i++) {
601→    const line = lines[i];
602→    if (typeof line !== "string") {
603→      continue;
604→    }
605→    const trimmed = line.trim();
606→    if (trimmed.startsWith("## ")) {
607→      break;
608→    }
609→    if (trimmed.startsWith("- Plan Status:")) {
610→      insertIndex = i + 1;
611→      break;
612→    }
613→  }
614→
615→  lines.splice(insertIndex, 0, statusLine);
616→  return lines.join("\n");
617→}
618→
619→function buildReviewMarkdown(report: ReviewReport): string {
620→  const lines: string[] = [];
621→  lines.push("# Automated Plan Review");
622→  lines.push("");
623→  lines.push(`- Plan: \`${report.planPath}\``);
624→  lines.push(`- Reviewed At: ${report.reviewedAt}`);
625→  lines.push(`- Model: ${report.model}`);
626→  lines.push(`- Verdict: **${report.verdict}**`);
627→  lines.push("");
628→
629→  for (const reviewer of report.reviewers) {
630→    lines.push(`## ${reviewer.reviewer}`);
631→    lines.push(`- Score: ${reviewer.score}/5`);
632→    lines.push(`- Summary: ${reviewer.summary}`);
633→    lines.push("");
634→
635→    if (reviewer.findings.length === 0) {
636→      lines.push("- Findings: none");
637→      lines.push("");
638→      continue;
639→    }
640→
641→    lines.push("### Findings");
642→    for (const finding of reviewer.findings) {
643→      lines.push(`- [${finding.severity}] ${finding.title}`);
644→      lines.push(`  - Detail: ${finding.detail}`);
645→      if (finding.suggestion.length > 0) {
646→        lines.push(`  - Suggestion: ${finding.suggestion}`);
647→      }
648→    }
649→    lines.push("");
650→  }
651→
652→  return `${lines.join("\n").trim()}\n`;
653→}
