655→export {
656→  buildReviewMarkdown,
657→  buildReviewMarker,
658→  computePlanHash,
659→  createFallbackResult,
660→  decideVerdict,
661→  extractTargetPath,
662→  extractLatestReviewMarker,
663→  isPlanFile,
664→  parseFindingsFromVerdict,
665→  parseReviewerResult,
